import React, { useState } from 'react';
import { FaEnvelope, FaLock } from 'react-icons/fa';
import './LoginForm.css';

const LoginForm = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const handleSubmit = async (event) => {
    event.preventDefault();

    try {
      // Send a request to your gateway service for authentication
      console.log("[*] Logging in!!!")
      console.log(email + " : " + password)
      const basicAuth = 'Basic ' + btoa(email + ':' + password);
      const request = {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': basicAuth
        },
          body: JSON.stringify({
            email: email,
            password: password
          })
        };
      const response = await fetch('http://foodwastereduction.com/login', request)
      const data = await response.json();
      const token = data.token;

      // Redirect the user to the dashboard page with the JWT token in the cookie
      console.log(token)
      document.cookie = `jwt=${token}; HttpOnly;`;
      window.location.href = '/dashboard';
    } catch (error) {
      
      console.log(error);
    }
  }

  return (
    <div className="login-container">
      <form onSubmit={handleSubmit}>
        <h2>Login</h2>
        <div className="form-group">
          <label htmlFor="email"><FaEnvelope /> Email address:</label>
          <input
            type="email"
            className="form-control"
            id="email"
            value={email}
            onChange={(event) => setEmail(event.target.value)}
          />
        </div>
        <div className="form-group">
          <label htmlFor="password"><FaLock /> Password:</label>
          <input
            type="password"
            className="form-control"
            id="password"
            value={password}
            onChange={(event) => setPassword(event.target.value)}
          />
        </div>
        <div className="form-group form-check">
          <label className="form-check-label">
            <input className="form-check-input" type="checkbox" /> Remember me
          </label>
        </div>
        <button type="submit" className="btn btn-primary">Submit</button>
      </form>
    </div>
  );
}

export default LoginForm;
